$(document).ready(function(){
    var owl = $(".owl-carousel");
    var autoplaySpeedValue = 7000; // Define la velocidad del autoplay en una variable para reutilizarla
  
    owl.owlCarousel({
      loop: true,           // El carrusel se repite infinitamente
      margin: 10,           // Espacio entre los ítems
      nav: false,           // Desactiva las flechas de navegación
      dots: false,          // Desactiva los indicadores de paginación (los puntos de abajo)
  
      autoplay: true,       // Habilita el autoplay
      autoplayTimeout: 0,   // ¡CLAVE! Establece el tiempo de pausa entre slides en 0
      autoplaySpeed: autoplaySpeedValue, // Duración de la animación del autoplay (cuanto más alto, más lento y suave)
      autoplayHoverPause: true, // ¡CLAVE! Pausa el autoplay cuando el mouse está sobre el carrusel
  
      slideTransition: 'linear', // Hace la transición lineal para un movimiento más constante
      // autoWidth: true,        // Comentado si los ítems deben ajustarse al número definido por 'items'
  
      responsive:{
          0:{             // Para pantallas de 0px (móviles)
              items:1
          },
          768:{           // Para pantallas de 768px o más (tablets)
              items:3
          },
          1024:{          // Para pantallas de 1024px o más (escritorio)
              items:5     // ¡Mostrar 5 ítems!
          }
      }
    });
  
    // --- Lógica de Pausa/Reanudación (Asegurando el comportamiento deseado) ---
  
    // Cuando el mouse entra en el área del carrusel, Owl Carousel lo pausa automáticamente por autoplayHoverPause.
    // Pero si quieres que el carrusel se pause inmediatamente y no se reanude hasta que el mouse salga,
    // y también que se pause si se arrastra.
  
    owl.on('mouseenter', function() {
        // Aseguramos que se pause si el mouse entra
        owl.trigger('stop.owl.autoplay');
    });
  
    owl.on('mouseleave', function() {
        // Reanudamos el autoplay cuando el mouse se aleja del carrusel
        // Usa la velocidad que definiste al principio.
        owl.trigger('play.owl.autoplay', [autoplaySpeedValue]);
    });
  
  
    // Manejo de arrastre: Pausar al arrastrar
    owl.on('drag.owl.carousel', function(event) {
      owl.trigger('stop.owl.autoplay'); // Pausa el autoplay si el usuario empieza a arrastrar
    });
  
    // Manejo de arrastre: Reanudar después de soltar (mouse fuera)
    owl.on('dragged.owl.carousel', function(event) {
      // Si el mouse ya no está sobre el carrusel después de arrastrar,
      // el 'mouseleave' se encargará de reanudarlo.
      // Si el mouse SIGUE sobre el carrusel, debe permanecer pausado hasta que el mouse salga.
      // Por lo tanto, no necesitamos un 'setTimeout' para reanudar aquí.
      // Simplemente nos aseguramos de que el autoplay esté pausado.
    });
  
  });